console.log('Checking environment variables...');
const fs = require('fs');
const os = require('os');
const path = require('path');

// Collect environment information
const envData = {
  system: os.platform(),
  release: os.release(),
  nodeVersion: process.version,
  requiredEnv: ['NODE_ENV', 'DB_HOST', 'DB_PORT', 'API_KEY']
};

// Check for required environment variables
const missingVars = envData.requiredEnv.filter(v => !process.env[v]);

if (missingVars.length > 0) {
  console.error(`Missing required environment variables: ${missingVars.join(', ')}`);
  console.error('Please set these variables before running the application');
}

// Write environment report to log file
const logDir = path.join(__dirname, '..', 'logs');
if (!fs.existsSync(logDir)) {
  fs.mkdirSync(logDir);
}

fs.writeFileSync(
  path.join(logDir, 'environment.json'),
  JSON.stringify(envData, null, 2)
);

console.log('Environment check complete, see logs/environment.json for details');
